INSERT INTO `ecjia_article` VALUES ('71', '27', '如何注册账号？', '<p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\"><strong>若您还没有账号，请点击注册，详细操作步骤如下： <br/></strong></span></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\"><strong><br/></strong></span></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\">1. 打开多商户首页，在左上方点击Logo进入左菜单然,后点击“请先登录”进入登陆界面。&nbsp; <span style=\\\"font-size: 18px;\\\"><br/><br/></span></span></p><div align=\\\"center\\\"><p><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150915/14422873415391.png\\\" title=\\\"14422873415391.png\\\" alt=\\\"QQ截图20150915112140.png\\\"/></p><p><br/></p></div><div align=\\\"center\\\"><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150915/14422873584844.png\\\" title=\\\"14422873584844.png\\\" alt=\\\"QQ截图20150915112200.png\\\"/></div><p><br/><br/><br/>2、点击右上角的“注册”链接,进行注册账号。<span style=\\\"font-size: 12px;\\\"><br/><br/></span></p><div align=\\\"center\\\"><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150914/14422185979361.png\\\" title=\\\"14422185979361.png\\\" alt=\\\"SQQ1.png\\\"/></div><p><br/><br/><br/></p><p><br/></p><p><br/></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\">3、输入注册手机号点击下一步。<span style=\\\"font-size: 18px;\\\"></span><span style=\\\"font-size: 12px;\\\"><br/><br/></span></span></p><div align=\\\"center\\\"><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150914/14422187814922.jpg\\\" title=\\\"14422187814922.jpg\\\" alt=\\\"SQQ2.jpg\\\"/></div><p><br/><br/><br/></p><p><br/></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\">4、发送验证码到手机。<br/><br/></span></p><div align=\\\"center\\\"><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150914/14422199013142.png\\\" title=\\\"14422199013142.png\\\" alt=\\\"sqq66.png\\\"/></div><p><br/><br/><br/></p><p><br/></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\">5、输入接收到的验证码。<br/><br/><span style=\\\"font-family: 微软雅黑,Microsoft YaHei; font-size: 12px;\\\"></span></span></p><div align=\\\"center\\\"><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150914/14422199118472.png\\\" title=\\\"14422199118472.png\\\" alt=\\\"sqq5.png\\\"/></div><p><br/><br/><br/></p><p><br/></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\">6、填写手机注册密码。<br/><br/><span style=\\\"font-family: 微软雅黑,Microsoft YaHei; font-size: 12px;\\\"></span></span></p><div align=\\\"center\\\"><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150914/14422190988074.png\\\" title=\\\"14422190988074.png\\\" alt=\\\"SQQ4.png\\\"/></div><p><br/><br/><br/></p><p><br/></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\">7、注册成功，手机账号生成，跳转到用户中心。<span style=\\\"font-family: 微软雅黑,Microsoft YaHei; font-size: 12px;\\\"><br/><br/></span></span></p><div align=\\\"center\\\"><img src=\\\"http://test.b2b2c.ecjia.com/content/uploads/data/descimg/20150914/14422191109051.png\\\" title=\\\"14422191109051.png\\\" alt=\\\"SQQ5.png\\\"/></div><p><br/></p><p style=\\\"text-align: left;\\\"><span style=\\\"font-size: 18px; font-family: 微软雅黑,Microsoft YaHei;\\\"><br/></span></p>', '_SHOPHELP', '', '', '0', '1', '1442186272', '', '0', '', '');
