INSERT INTO `ecjia_mail_templates` VALUES ('6', 'send_bonus', '0', '发红包', '亲爱的{$user_name}您好！\n\n恭喜您获得了{$count}个红包，金额{if $count > 1}分别{/if}为{$money}\n\n{$shop_name}\n{$send_date}\n', '1156491184', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('7', 'group_buy', '1', '团购商品', '<p>亲爱的{$consignee}，您好！<br/><br/>您于{$order_time}在本店参加团购商品活动，所购买的商品名称为：{$goods_name}，数量：{$goods_number}，订单号为：{$order_sn}，订单金额为：{$order_amount}<br/><br/>此团购商品现在已到结束日期，并达到最低价格，您现在可以对该订单付款。<br/><br/>请尽快登录到用户中心，查看您的订单详情信息。 <br/><br/>{$shop_name} <br/><br/>{$send_date}</p>', '1441161456', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('8', 'register_validate', '1', '邮件验证', '{$user_name}您好！<br><br>\r\n\r\n这封邮件是 {$shop_name} 发送的。你收到这封邮件是为了验证你注册邮件地址是否有效。如果您已经通过验证了，请忽略这封邮件。<br>\r\n请点击以下链接(或者复制到您的浏览器)来验证你的邮件地址:<br>\r\n<a href=\"{$validate_email}\" target=\"_blank\">{$validate_email}</a><br><br>\r\n\r\n{$shop_name}<br>\r\n{$send_date}', '1162201031', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('10', 'attention_list', '0', '关注商品', '亲爱的{$user_name}您好~\n\n您关注的商品 : {$goods_name} 最近已经更新,请您查看最新的商品信息\n\n{$goods_url}\r\n\r\n{$shop_name} \r\n{$send_date}', '1183851073', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('11', 'remind_of_new_order', '0', '新订单通知', '亲爱的店长，您好：\r\n   快来看看吧，又有新订单了。\r\n    订单号:{$order.order_sn} \r\n 订单金额:{$order.order_amount}，\r\n 用户购买商品:{foreach from=$goods_list item=goods_data}{$goods_data.goods_name}(货号:{$goods_data.goods_sn})    {/foreach} \r\n\r\n 收货人:{$order.consignee}， \r\n 收货人地址:{$order.address}，\r\n 收货人电话:{$order.tel} {$order.mobile}, \r\n 配送方式:{$order.shipping_name}(费用:{$order.shipping_fee}), \r\n{if $order.pay_id eq 1}\r\n 付款方式:{$order.pay_name}(费用:{$order.surplus})。\r\n{else}\r\n 付款方式:{$order.pay_name}(费用:{$order.pay_fee})。\r\n{/if}\r\n\r\n               系统提醒\r\n               {$send_date}', '1433268639', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('12', 'goods_booking', '1', '缺货回复', '亲爱的{$user_name}。你好！</br></br>{$dispose_note}</br></br>您提交的缺货商品链接为</br></br><a href=\"{$goods_link}\" target=\"_blank\">{$goods_name}</a></br><br>{$shop_name} </br>{$send_date}', '0', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('13', 'user_message', '1', '留言回复', '亲爱的{$user_name}。你好！</br></br>对您的留言：</br>{$message_content}</br></br>店主作了如下回复：</br>{$message_note}</br></br>您可以随时回到店中和店主继续沟通。</br>{$shop_name}</br>{$send_date}', '0', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('14', 'recomment', '1', '用户评论回复', '亲爱的{$user_name}。你好！</br></br>对您的评论：</br>“{$comment}”</br></br>店主作了如下回复：</br>“{$recomment}”</br></br>您可以随时回到店中和店主继续沟通。</br>{$shop_name}</br>{$send_date}', '0', '0', 'template');
INSERT INTO `ecjia_mail_templates` VALUES ('15', 'sale_notice', '1', '降价通知', '<p>{$user_name}您好！<br />\r\n<br />\r\n您期望的商品 ({$goods_name}) 已降价，请点击以下链接(或者复制到您的浏览器):<br />\r\n<br />\r\n<a target=\"_blank\" href=\"{$goods_link}\">{$goods_link}</a><br />\r\n<br />\r\n此信息无需回复！<br />\r\n<br />\r\n<br />\r\n{$send_date}</p>', '1407196355', '0', 'template');
