INSERT INTO `ecjia_mail_templates` VALUES ('18', 'sms_receipt_verification', '0', '订单收货验证码', '尊敬的{$user_name} ，您在我们网站已成功下单。订单号：{$order_sn}，收货验证码为：{$code}。请保管好您的验证码，以便收货验证。', '1471728837', '0', 'sms');
