INSERT INTO `ecjia_mail_templates` VALUES ('24', 'sms_jion_merchant', '0', '审核通过', '尊敬的{$user_name}，恭喜您通过【{$shop_name}】平台商家入驻审核。账号：{$mobile}， 密码：{$password}，请不要把密码泄露给其他人。如有问题请拨打客服电话：{$service_phone}。', '1476930217', '0', 'sms');
